mapper.update:
update user_info
user_info_nick = #{userInfoNick}, user_info_name = #{userInfoName}, user_info_sex = #{userInfoSex}, user_info_age = #{userInfoAge}, user_info_id_number = #{userInfoIdNumber}, user_info_id_type = #{userInfoIdType}, user_info_email = #{userInfoEmail}, user_info_province = #{userInfoProvince}, user_info_address = #{userInfoAddress},
where user_login_id = #{userLoginId}

update user_login
user_phone = #{userPhone}, user_password = #{userPassword}, user_last_time = #{userLastTime}, user_status = #{userStatus}, user_session_id = #{userSessionId}, user_last_login_ip = #{userLastLoginIp}
where user_id = #{userId}
